previousValue=null;productsTotals=document.querySelectorAll(".manufacture_part_products_form_total");for(let i=0;i<productsTotals.length;i++){let productTotal=productsTotals[i];if(productTotal){productTotal.addEventListener("focus",resetValue,false);productTotal.addEventListener("blur",restoreValue,false);function resetValue(){previousValue=productTotal.value;productTotal.value=""}function restoreValue(){if(productTotal.value===""){productTotal.value=previousValue}}productTotal.addEventListener("input",orderModalCounter.debounce(300));productTotal.closest(".total-count-container").querySelector(".plus").addEventListener("click",(()=>{console.log("plus");let price_total=productTotal;let result=price_total.value*1;let max=price_total.dataset.max*1;if(result<max){result=result+1;productTotal.value=result}}));productTotal.closest(".total-count-container").querySelector(".minus").addEventListener("click",(()=>{console.log("minus");let price_total=productTotal;let result=price_total.value*1;if(result>1){result=result-1;productTotal.value=result}}));function orderModalCounter(){let result=this.value*1;let max=this.dataset.max*1;if(isNaN(result)||result===0){productTotal.value=1;result=1}if(result>max){productTotal.value=max;result=max}}}}